export class FlowValidator {
  static checkFlowCapacity(capacity) {
    return control => {
      if (capacity.getValue() != 0) {
        const violated = capacity.getValue() < parseInt(control.value);
        return violated ? {
          "checkFlowCapacity": true
        } : null;
      }
      return null;
    };
  }
  static checkSourceSinkId(numVertices) {
    return control => {
      const violated = parseInt(control.value) > numVertices || parseInt(control.value) <= 0;
      return violated ? {
        "checkSourceSinkId": true
      } : null;
    };
  }
}
