export class Edge {
  constructor() {}
  setEdge(source, target, capacity) {
    this.source = source;
    this.target = target;
    this.capacity = capacity;
    this.flow = 0;
  }
  // Add flow by a specified number
  addFlowByNum(bottleNeck) {
    this.flow += bottleNeck;
  }
  setEdgeValue(flow, capacity) {
    this.flow = flow;
    this.capacity = capacity;
  }
}
